<?phpnamespace LinkSoft\Source;interface QuoteInterface{    /**     * @param string $instrument 证券标识     * @param string $lang 语言 zh-CN|en     * @return array     */    public function detail(string $instrument, string $lang): array;    /**     * 获取行情接口     * @param string $instrument 证券标识     * @param string $lang 语言 zh-CN|en     * @return array     */    public function detailV2(string $instrument, string $lang): array;    /**     * 批量获取行情     * @param string $instrument 证券标识     * @param string $lang 语言 zh-CN|en     * @param int $format 格式     * @return array     */    public function batchDetail(string $instrument, string $lang, int $format): array;    /**     * 批量获取行情V2     * @param string $instrument 证券标识     * @param string $lang 语言 zh-CN|en     * @param int $format 格式     * @return array     */    public function batchDetailV2(string $instrument, string $lang, int $format): array;    /**     * 获取逐笔交易     * @param string $instrument 证券标识     * @param int $offset 起始点     * @param int $limit 限制数量     * @return array     */    public function tick(string $instrument, int $offset, int $limit): array;    /**     * 深度报价.     * @param string $instrument 证券标识     * @param int $depth 深度     * @return array     */    public function depthQuota(string $instrument, int $depth): array;    /**     * 资金流向.     * @return array     */    public function amtFlow(): array;    /**     * 资金流向K线     * @param int $type 1:北向概况 2:南向概况     * @return array     */    public function amtFlowKline($type): array;    /**     * 深度报价.     * @param string $instrument 证券标识     * @param string $lang 语言     * @return array     */    public function broker(string $instrument, string $lang): array;    /**     * 券商列表.     * @return array     */    public function brokers(): array;    /**     * 获取论证数据     * @param string $securityTypes 证券标识     * @param string $symbol 标的股票     * @param string $field 排序字段     * @param string $order 排序类型     * @param int $page 页码     * @param int $pageSize 每页条数     * @return array     */    public function turbineSearch(string $securityTypes, string $symbol, string $field, string $order, int $page, int $pageSize): array;}